mapper.select:
SELECT * FROM ZPAY_HISTORY
WHERE member_id = #{member_id}
ORDER BY zpay_time DESC

mapper.insert:
INSERT INTO 
	ZPAY_HISTORY(
		zpay_history_idx
		, zpay_idx
		, zpay_amount
		, zpay_time
		, zpay_balance
		, zpay_deal_type
		, member_id)
	VALUES(
		null
		, #{zpay_idx}
		, #{zpay_amount}
		, now()
		, #{zpay_balance} + #{zpay_amount}
		, #{zpay_deal_type}
		, #{member_id}
	)
