mapper.insert:
INSERT
	INTO ALARM
	VALUES
		(
		NULL
		, NOW()
		, #{member_id}
		, #{alarm_message}
		, #{alarm_link}
		, 'N'
		, #{alarm_type}
		)

mapper.select:
SELECT * FROM ALARM WHERE member_id = #{member_id} AND alarm_time >= SUBDATE(NOW(), INTERVAL 3 MONTH)
AND alarm_type = #{category}
ORDER BY alarm_idx DESC LIMIT #{startRow} , #{listLimit}
