mapper.insert:
INSERT
	INTO CS (
			cs_subject    
			, cs_content   
			, cs_date       
			, cs_type		
			, cs_file		
			, cs_info_idx	
			, member_id		
	)
	VALUES (
			#{cs_subject}
			, #{cs_content}
			, now()
			, #{cs_type}
			, #{cs_file}
			, #{cs_info_idx}
			, #{member_id} 
	);

mapper.select:
SELECT cs_subject , cs_content , cs_type FROM CS
WHERE cs_type IN ('이용정책', '공통', '중고판매', '경매판매', '경매구매', 'ZPAY')
WHERE cs_type = #{cs_type}
