mapper.update:
UPDATE ZPAY
SET zpay_passwd = #{new_zpay_passwd}
WHERE member_id = #{member_id}

mapper.select:
SELECT * FROM ZPAY_HISTORY
WHERE member_id = #{member_id}
ORDER BY zpay_time DESC

mapper.insert:
INSERT INTO 
	ZPAY_HISTORY(
		zpay_history_idx
		, zpay_idx
		, zpay_time
		, zpay_amount
		, zpay_balance
		
			, order_auction_idx
		
		
			, order_secondhand_idx
		
		, zpay_deal_type
		, member_id
		)
	VALUES(
		null
		, #{zpay_idx}
		, now()
		
			
				, #{zpay_amount} * 1.03
				, #{zpay_balance} + #{zpay_amount} * 1.03		
			
			
				, #{zpay_amount}
				, #{zpay_balance} - #{zpay_amount}
			
			
				, #{zpay_amount}						
				, #{zpay_balance} + #{zpay_amount}						
			
			
				, #{zpay_amount}						
				, #{zpay_balance} - #{zpay_amount}
							
		
		
			, #{order_auction_idx}
		
		
			, #{order_secondhand_idx}
		
		, #{zpay_deal_type}
		, #{member_id}
	)

