				, null
				, null
				, null
				, #{zero_account_balance} - #{zero_account_amount}
				, #{zero_account_amount}
			
			
				, #{zpay_history_idx}
				, #{order_secondhand_idx}
				, null
				, null
				, #{zero_account_balance} + #{zero_account_amount}
				, #{zero_account_amount}
			
			
				, #{zpay_history_idx}
				, null
				, #{order_auction_idx} 
				, null
				, #{zero_account_balance} + #{zero_account_amount}
				, #{zero_account_amount}
			
			
				, null
				, null
				, null
				, #{auction_idx}
				, #{zero_account_balance} + #{zero_account_amount}
				, #{zero_account_amount}
			
			
				, null
				, null
				, null
				, #{auction_idx}
				, #{zero_account_balance} - #{zero_account_amount} * 0.9
				, #{zero_account_amount}
			
		
		, now()
		, #{zero_account_type}
	)

